mapper.select:
select count(*)
From member
where id = #{id}

select count(*)
From member
where email = #{email}

mapper.update:
update member
set file_name=#{filename}
where id = #{id}

update member
set pw=#{pw},
    region=#{region},
    name=#{name},
    region1=#{region1}
where id = #{id}

update member
set file_name=null
where id = #{id}

mapper.select:
select *
from (select @rownum:=@rownum+1 as rownum,A.*
      from faqpost A, (select @rownum := 0) R
      order by f_no desc) list
where rownum  >= #{startNo}
  and rownum  <= #{endNo}

select *
from (select @rownum:=@rownum+1 as rownum,A.*
      from faqpost A, (select @rownum := 0) R
      where
          ${searchType} like concat('%'
          , #{searchKeyword}
          , '%')
      order by f_no desc) list
where rownum >= #{startNo}
  and rownum <= #{endNo}

mapper.update:
update faqpost
set view_c=view_c + 1
where f_no = ${f_no}

mapper.select:
select count(*)
from faqpost
where ${searchType} like concat('%', #{searchKeyword}, '%');
